      <header className="main-header">
        <div className="wrapper-header header-transparent" id="themes-header">
          <div className="container">
            <div className="header-middle row-flex flex-flow-header align-items-center">
              {/* Logo */}
              <div className="col-md-2 col-sm-4 col-xs-12">
                <div className="main-header--logo fade-box" itemScope="" itemType="http://schema.org/Organization">
                  <a href="/">
                    <h1 className="logo">
                      <img
                        itemProp="logo"
                        src="/assets/images/logoShop.png"
                        style={{ width: "200px" }}
                        alt="KENTA.VN"
                        className="img-responsive logoimg lazyload"
                      />
                    </h1>
                  </a>
                </div>
              </div>

              {/* Search and Cart */}
              <div className="col-md-2 col-sm-4 col-xs-12">
                <div className="main-header--action row-flex justify-content-end">
                  <div className="action--search" id="site-search-handle" style={{ width: "200px", zIndex: 9999 }}>
                    {/* Đặt ô tìm kiếm ở đây */}
                    <ReactSearchAutocomplete items={items} onSearch={handleOnSearch} onSelect={handleOnSelect} formatResult={formatResult} />
                  </div>
                  <div className="action-cart" id="site-cart-handle">
                    {/* Đặt giỏ hàng ở đây */}
                    <a href="/cart">
                      <span className="cart-menu" aria-hidden="true">
                        <svg
                          version="1.1"
                          className="svg-cart"
                          xmlns="http://www.w3.org/2000/svg"
                          xmlnsXlink="http://www.w3.org/1999/xlink"
                          viewBox="0 0 24 27"
                          style={{ enableBackground: "new 0 0 24 27" }}
                          xmlSpace="preserve"
                        >
                          <g>
                            <path d="M0,6v21h24V6H0z M22,25H2V8h20V25z" />
                          </g>
                          <g>
                            <path d="M12,2c3,0,3,2.3,3,4h2c0-2.8-1-6-5-6S7,3.2,7,6h2C9,4.3,9,2,12,2z" />
                          </g>
                        </svg>
                        <span className="count-holder">
                          <span className="count">{productsCount}</span>
                        </span>
                      </span>
                    </a>
                  </div>
                </div>
              </div>


              <div className="col-md-8 col-sm-4 col-xs-12">
                <div className="main-header--menu">
                  <nav className="navbar-mainmenu">
                    <ul className="list-mainmenu">
                      {categories.map((categoryWithSubcategories) => (
                        <li
                          className="has-submenu "
                          key={categoryWithSubcategories.category._id}
                        >
                          <a
                            href={
                              "/collection/" +
                              categoryWithSubcategories.category.name
                            }
                            title={categoryWithSubcategories.category.name}
                          >
                            {categoryWithSubcategories.category.name}
                            <i
                              className="fa fa-chevron-down"
                              aria-hidden="true"
                            />
                          </a>
                          <ul className="list-submenu">
                            {categoryWithSubcategories.subcategories.map(
                              (subcategory) => (
                                <li key={subcategory._id}>
                                  <a
                                    href={
                                      "/collection/" +
                                      categoryWithSubcategories.category.name +
                                      "/" +
                                      subcategory.name
                                    }
                                    title={subcategory.name}
                                  >
                                    {subcategory.name}
                                  </a>
                                </li>
                              )
                            )}
                          </ul>
                        </li>
                      ))}
                    </ul>
                  </nav>
                </div>
              </div>
            </div>
          </div>
        </div>
      </header>



      <div id="site-nav--mobile" className="site-nav style--sidebar">
        <div id="site-navigation" className="site-nav-container">
          <div className="site-nav-container-last">
            <p className="title">Menu</p>
            <div className="main-navbar">
              <nav className="primary-menu">
                <ul className="menu-collection">
                  {categories.map((categoryWithSubcategories) => (
                    <li
                      className="navi1 has-sub nav-level0"
                      key={categoryWithSubcategories.category._id}
                    >
                      <a
                        href={
                          "/collection/" +
                          categoryWithSubcategories.category.name
                        }
                        title={categoryWithSubcategories.category.name}
                      >
                        {categoryWithSubcategories.category.name}
                      </a>
                      <span className="icon-subnav">
                        <i className="fa fa-angle-down"></i>
                      </span>
                      <ul className="submenu subnav-children">
                        {categoryWithSubcategories.subcategories.map(
                          (subcategory) => (
                            <li className="navi2" key={subcategory._id}>
                              <a
                                href={
                                  "/collection/" +
                                  categoryWithSubcategories.category.name +
                                  "/" +
                                  subcategory.name
                                }
                                title={subcategory.name}
                              >
                                {subcategory.name}
                              </a>
                            </li>
                          )
                        )}
                      </ul>
                    </li>
                  ))}
                  {role === "admin" && (
                    <li className="navi1 has-sub nav-level0">
                      <a style={{ cursor: "pointer" }} title="Quản lí">
                        Quản lí
                      </a>
                      <span className="icon-subnav">
                        <i className="fa fa-angle-down"></i>
                      </span>
                      <ul className="submenu subnav-children">
                        <li className="navi2">
                          <a href="/manage-user" title="Quản lí người dùng">
                            Người dùng
                          </a>
                        </li>
                        <li className="navi2">
                          <a href="/manage-product" title="Quản lý sản phẩm">
                            Sản phẩm
                          </a>
                        </li>
                        <li className="navi2">
                          <a href="/manage-order" title="Quản lý đơn hàng">
                            Đơn hàng
                          </a>
                        </li>
                        <li className="navi2">
                          <a href="/manage-category" title="Quản lý danh mục">
                            Danh mục
                          </a>
                        </li>
                        <li className="navi2">
                          <a href="/manage-revenue" title="Quản lý doanh thu">
                            Doanh thu
                          </a>
                        </li>
                        <li className="navi2">
                          <a href="/manage-rating" title="Quản lý đánh giá">
                            Đánh giá
                          </a>
                        </li>
                      </ul>
                    </li>
                  )}
                  {!isLoggedIn ? (
                    <li className="navi1 ">
                      <a href="/login" title="Đăng nhập">
                        Đăng nhập
                      </a>
                    </li>
                  ) : (
                    <li className="navi1 has-sub nav-level0">
                      <a href="/manage-profile" title="Tài khoản">
                        {firstname + " " + lastname}
                      </a>
                      <span className="icon-subnav">
                        <i className="fa fa-angle-down"></i>
                      </span>
                      <ul className="submenu subnav-children">
                        <li className="navi2">
                          <a href="/manage-profile" title="Tài khoản">
                            Tài khoản
                          </a>
                        </li>
                        <li className="navi2">
                          <a href="/purchase-order" title="Đơn hàng của bạn">
                            Đơn hàng của bạn
                          </a>
                        </li>
                        <li className="navi2">
                          <a
                            onClick={handleLogout}
                            title="Đăng xuất"
                            style={{ cursor: "pointer" }}
                          >
                            Đăng xuất
                          </a>
                        </li>
                      </ul>
                    </li>
                  )}
                </ul>
              </nav>
            </div>
          </div>
        </div>
        <button
          id="site-close-handle"
          className="site-close-handle"
          onClick={removeClassFromElement}
          title="Đóng"
        >
          <span className="hamburger-menu active" aria-hidden="true">
            <span className="bar animate"></span>
          </span>
        </button>
      </div>
    </>
  );
}

export default Header;
